mapper.update:
UPDATE order_item SET order_item_or_delete=1
WHERE order_item_id IN

    #{id}

mapper.select:
SELECT count(1) FROM product p,division d,subdivision sb,USER u,order_item oi WHERE p.`division_id` = d.`division_id` AND p.`subdivision_id` = sb.`subdivision_id` AND p.`product_id` = oi.`product_id` AND u.`user_id` = oi.`user_id` AND oi.`order_item_or_delete` = 0
AND (u.`user_real_name` LIKE CONCAT("%",#{user.userRealName},"%") or u.user_username like CONCAT("%",#{user.userRealName},"%"))
AND d.`division_id` = #{product.division.divisionId}
AND sb.`subdivision_id` = #{subdivision.subdivisionId}
AND p.`product_name` LIKE CONCAT("%",#{product.productName},"%")

SELECT p.`product_name`,p.`product_image`,p.`product_shop_price`, d.`division_id`,d.`division_name`,sb.`subdivision_id`,sb.`subdivision_name`, u.`user_username`,u.`user_real_name`, oi.`order_item_id`,oi.`order_item_count`,oi.`order_item_subtotal`,oi.`order_item_date`,oi.`order_item_statu` FROM product p,division d,subdivision sb,USER u,order_item oi WHERE p.`division_id` = d.`division_id` AND p.`subdivision_id` = sb.`subdivision_id` AND p.`product_id` = oi.`product_id` AND u.`user_id` = oi.`user_id` AND oi.`order_item_or_delete` = 0
AND (u.`user_real_name` LIKE CONCAT("%",#{condition.user.userRealName},"%") or u.user_username like CONCAT("%",#{condition.user.userRealName},"%"))
AND d.`division_id` = #{condition.product.division.divisionId}
AND sb.`subdivision_id` = #{condition.product.subdivision.subdivisionId}
AND p.`product_name` LIKE CONCAT("%",#{condition.product.productName},"%")
limit #{start},#{limit}
