mapper.update:
update subdivision
set subdivision_or_delete = '1'
where subdivision_id=#{subdivisionId}

mapper.select:
select count(subdivision.subdivision_id) from subdivision,division where subdivision_or_delete=0 and subdivision.division_id = division.division_id
and subdivision_name like concat("%",#{subdivisionName},"%")
and division.division_id = #{division.divisionId}

select subdivision.subdivision_name, subdivision.subdivision_id, division.division_id, division.division_name from subdivision,division where subdivision_or_delete=0 and subdivision.division_id = division.division_id
and subdivision_name like concat("%",#{condition.subdivisionName},"%")
and division.division_id = #{condition.division.divisionId}
limit #{start},#{limit}
